mapper.select:
 insert into user_color(
	user_color_no
	,color_no
	,user_id
 )
 values(
 	#{userColorInsert}
 	,#{userColorNo}
 	,#{userId}
 );

 insert into user_style(
	user_style_no
	,style_no
	,user_id
 )
 values(
 	#{userStyleInsert}
 	,#{userStyleNo}
 	,#{userId}
 );

select MAX(CAST(substring(user_color_no, 12) AS DECIMAL))
from user_color
;

select MAX(CAST(substring(user_style_no, 12) AS DECIMAL))
from user_style
;

DELETE FROM user_color 
WHERE user_id = #{userId}
;

DELETE FROM user_style 
WHERE user_id = #{userId}
;
