mapper.select:
SELECT ad_cost_value
FROM ad_cost
WHERE ad_cost_no = #{adtype}

SELECT ad_dc_rate
FROM ad_dc
WHERE ad_dc_no = #{adCostNo}

mapper.update:
 		UPDATE ad_contract
		SET ad_contract_stat='취소요청'
		WHERE ad_contract_no = #{contractno}

 		UPDATE ad_contract
		SET ad_contract_stat='광고등록대기'
		WHERE ad_contract_no = #{contractno}

 	UPDATE ad_contract
	SET ad_contract_stat='계약만료'
	WHERE ad_contract_no = #{contractno}

 	UPDATE ad_contract
	SET ad_contract_stat='광고대기'
	WHERE ad_contract_no = #{contractno}
