mapper.insert:
INSERT INTO mall_sale
(
user_id
,mall_pro_no
,mall_sale_amount
,mall_sale_price
,mall_sale_total
,mall_sale_date
,coo_contract_no
)
VALUES 
(
#{userId}
,#{mallProNo}
,#{mallSaleAmount}
,#{mallSalePrice}
,#{mallSaleTotal}
,now()
,(
	SELECT coo_contract_no 
	FROM coo_contract 
	WHERE coo_contract_code =#{cooContractCode}
	)
)

